package dev.finley.language;import dev.finley.HeadPlugin;import lombok.Getter;import lombok.Setter;import java.util.List;@Getterpublic class LanguageManager {    private final List<String> languages;    @Setter    private String selectedLanguage = "";    public LanguageManager() {        languages = HeadPlugin.getInstance().getMessageFile().getStringList("languages");        setup();    }    private void setup() {    }    public String changeLanguage(String language) {        String languagePath = HeadPlugin.getInstance().getConfig().getString("settings.language");        if (languagePath == null || languagePath.isBlank()) {            return "false-invalid_language";        }        if (language.equals(languagePath)) {            selectedLanguage = languagePath;        } else {            selectedLanguage = "en";            return "false-invalid_language";        }        return "true";    }}